mapper.select:

SELECT NUM, TITLE, HIREDATE
FROM (SELECT ROWNUM NUM, CONTENT, TITLE, HIREDATE	
		FROM	(SELECT TITLE, CONTENT, HIREDATE
				FROM NOTICE
				ORDER BY HIREDATE))
WHERE NUM BETWEEN #{startNum} AND #{endNum}
 
	
		
			
				AND  TITLE LIKE #{keyword}
			
			
				AND  CONTENT LIKE #{keyword}
			
		
	
 
ORDER BY NUM DESC
